const employees = queries.getData.data || {};
const map = {};

employees.forEach(e => {
  map[e.department] = (map[e.department] || 0) + 1;
});

return Object.keys(map).map(dept => ({
  x: dept,
  y: map[dept]
}));

const employees = queries.getDatasdata || [];

if (employees.length === 0) return 0;

const today = new Date();

const totalYears = employees.reduce((sum, emp) => {
  const joinDate = new Date(emp.date_of_joining);
  const diffMs = today - joinDate;
  const years = diffMs / (1000 * 60 * 60 * 24 * 365);
  return sum + years;
}, 0);

return Number((totalYears / employees.length).toFixed(2));

const employees = queries.getData.data || [];

if (employees.length === 0) return 0;

const totalAge = employees.reduce((sum, emp) => {
  return sum + Number(emp.age || 0);
}, 0);

return Number((totalAge / employees.length).toFixed(1));
